import java.util.Arrays;

public class ex6_20 {

	static int[] shuffle(int arr[]) {
		if(arr==null || arr.length==0) return arr;
		int tmp=0;
		for(int i=0;i&amp;lt;arr.length;i++) {
			int x = (int)(Math.random()*arr.length);
			tmp=arr[i];
			arr[i] = arr[x];
			arr[x] = tmp;
		}
		return arr;
	}

