package _2javajungsuktestEx6;
class SutdaCard{
	int num=3 ;
	boolean isKwang=true;

	SutdaCard(){
		this(1,true);
	}

	SutdaCard(int num, boolean isKwang){
		this.num=num;
		this.isKwang=isKwang;
	}
	String info() {
		return num+(isKwang?&quot;K&quot;:&quot;&quot;);
	}
}

