&amp;lt;configuration scan=&quot;true&quot; scanPeriod=&quot;60 seconds&quot;&amp;gt;  

    &amp;lt;springProperty scope=&quot;context&quot; name=&quot;LOG_LEVEL&quot; source=&quot;logging.level.root&quot;/&amp;gt;  

